import { Form, Input,Select,Modal,Table,Button, Tag } from 'antd';
import{EditOutlined,DeleteOutlined,SearchOutlined }from '@ant-design/icons';
import { Row, Col,DatePicker, Space } from 'antd';
import { useEffect, useState,useContext, createContext} from 'react';
import moment from 'moment'
import data from './records.json'
import  ComponentA from './ComponentA';
import ComponentB from './ComponentB';
import Count from './Count';
import Display from './Display';
import './App.css';
import 'antd/dist/antd.css';


export const store =createContext();
export const mall=createContext();

const { TextArea } = Input;


const App=()=> {
  const [storage, setStorage] = useState(data)
  const [storeName,setStoreName]=useState('')
  const [datavalue, setDataValue] = useState([
    {
      brandname:"nokia"

    },
    {
      brandname:"Mi"

    },
    {
      brandname:"one+" 

    },

  ])



  const getdata=()=>{


    // for (let index = 0; index < data.length; index++) {
    //   const element = data[index];
    //   console.log("==ele=",element)
    //   for (let index = 0; index < element.batters.length; index++) {
    //     const batter = element.batters[index];
    //     console.log("batter",batter)
    //   }
    // }

  }
  const submitHandlerStore=(e)=>{
    e.preventDefault();

    setStorage([...storage,{id:'',type:'',name:'',ppu:''}])
  }


  return (
     <div className="App">  
    <h1>cw solution</h1>
    <div>
    <store.Provider value={[datavalue, setDataValue]}>
      <center>
        {/* <ComponentA/>
        <ComponentB/> */}
        {/* <Button onClick={()=>setDataValue(datavalue+1)}>increment</Button>  */}
        <Count/> 
        <Display/>
      </center>
   </store.Provider>
    </div>
<br/>
    {/* <div>
        <Button onClick={getdata}>add</Button>

    </div> */}
     {console.log("=====",data)}
    {data && data.map(record=>{
      {console.log("=====",record)}
         return(
          <div key={record.id}> 
          {/* <Row gutter={16}>
          <Col className="gutter-row" span={6}>
          <Form.Item name="bUnitname" label="Business Unit*">  */}
             {<span>{record.type}</span>}<br/> 
           {/* </Form.Item>
          </Col> */}
          {/* </Row> */}
            <span>{record.name}</span><br/>
            <span>{record.ppu}</span><br/> 
            <form className="form" onSubmit={submitHandlerStore}>
                 <input text="text" onChange={(e)=>setStoreName(e.target.value)} placeholder="enter your brand"></input>
                 <input type="submit" value="Add"></input>
           </form>
            {console.log("====topping",record.topping[0].type)}

            <br/>
            {record.batters.batter && record.batters.batter.map(ele=>{
              <span>{console.log("ele",ele)}</span>
              return(
               <div style={{background:"pink"}}>
                   <span>{ele.id}</span><br/>
                   <span>{ele.type}</span>
              </div> 
              )
            })}  

             {record.topping && record.topping.map(text=>{
            <span>{console.log("text===",text)}</span>  
              return(
               <div key={text.id} style={{background:"blue"}} >  
                 <span>{text.type}</span><br/>         
                </div> 
              )
            })} 
          </div> 


        )
    })
    } 
  </div> 
  );
}

export default App;
